def data_path(config):
    return  'data/nda' + str(config['data']['n_datasets']) + '_nsa' + str(config['data']['n_samples']) + '_nfe' + str(config['data']['n_features']) + '_nin' + str(config['data']['n_informative']) + '_nta' + str(config['data']['n_targets']) + '_bia' + str(config['data']['bias']) + '_eff' + str(config['data']['effective_rank']) + '_tai' + str(config['data']['tail_strength']) + '_noi' + str(config['data']['noise']) + '_shu' + str(config['data']['shuffle']) + '_ran' + str(config['data']['random_state']) 

def lambda_path(config, datasetIndex):
    return  data_path(config) + '/' + str(datasetIndex) + '_tsi' + str(config['lambda']['data_prep']['train_test_val_split']['test_size']) + '_vsi' + str(config['lambda']['data_prep']['train_test_val_split']['val_size']) + '_ran' + str(config['lambda']['data_prep']['train_test_val_split']['random_state']) + '_shu' + str(config['lambda']['data_prep']['train_test_val_split']['shuffle']) + '_str' + str(config['lambda']['data_prep']['train_test_val_split']['stratify']) + '_bat' + str(config['lambda']['model_fit']['batch_size']) + '_epo' + str(config['lambda']['model_fit']['epochs']) + '_shu' +  str(config['lambda']['model_fit']['shuffle']) + '_cla' + str(config['lambda']['model_fit']['class_weight']) + '_sam' + str(config['lambda']['model_fit']['sample_weight']) + '_ini' + str(config['lambda']['model_fit']['initial_epoch']) + '_ste' + str(config['lambda']['model_fit']['steps_per_epoch']) + '_vst' + str(config['lambda']['model_fit']['validation_steps']) + '_vbs' + str(config['lambda']['model_fit']['validation_batch_size']) + '_vfr' + str(config['lambda']['model_fit']['validation_freq'])